import React from 'react';
import Layout from './hoc/Layout/Layout';
import BurgerBuilder from './containers/BurgerBuilder/BurgerBuilder'

class App extends Component {
  state = {
    show: true
  };

  render(){


    return (
      <div >
      <Layout>
      {this.state.show ? <BurgerBuilder/> : null}
      </Layout>
      </div>
    );
  }
}

export default App;
