import React, { Component } from 'react';

import Aux from '../../hoc/Aux/Aux'
import Burger from '../../components/Burger/Burger'
import BuildControls from '../../components/Burger/BuildControls/BuildControls'
import Modal from '../../components/UI/Modal/Modal'
import OrderSummary from '../../components/Burger/OrderSummary/OrderSummary'
import axios from '../../axios-orders.js'
import Spinner from '../../components/UI/Spinner/Spinner'
import withErrorHandler from '../../hoc/withErrorHandler/withErrorHandler';

// import Backdrop from '../../components/UI/Backdrop/Backdrop'

const INGREDIENT_PRICES = {
  salad: .25,
  cheese: .25,
  meat: .75,
  bacon: .50
}

class BurgerBuilder extends Component {
  state = {
    ingredients: null,
    totalPrice: .75,
    canBuy: false,
    isBuying: false,
    loading: false,
  }

  componentDidMount () {
    axios.get('https://react-my-burger-ab5c1.firebaseio.com/ingredients')
    .then(response => {
      this.setState({ingredients : response.data})
    })
  }

  purchaseHandler = () => {
    this.setState({isBuying: true});
  }

  updatePurchaseState (ingredients) {
    const sum = Object.keys(ingredients)
      .map(igKey => {
        return ingredients[igKey]
      })
      .reduce((sum, el) => {
        return sum + el;
      }, 0);
    this.setState({canBuy : sum > 0});
  }

  addIngredientHandler = (type) => {
    const updatedCount = this.state.ingredients[type] + 1;
    const updatedIngredients = {
      ...this.state.ingredients
    }
    updatedIngredients[type] = updatedCount;
    const newPrice = this.state.totalPrice + INGREDIENT_PRICES[type];
    this.setState({totalPrice: newPrice, ingredients: updatedIngredients})
    this.updatePurchaseState(updatedIngredients);

  }

  subtractIngredientHandler = (type) => {
    const oldCount = this.state.ingredients[type];
    if (oldCount <= 0) {
      return;
    }
    const newCount = oldCount -1
    const updatedIngredients = {
      ...this.state.ingredients
    };
    updatedIngredients[type] = newCount;
    const newPrice = this.state.totalPrice -INGREDIENT_PRICES[type];
    this.setState({totalPrice: newPrice, ingredients: updatedIngredients})
    this.updatePurchaseState(updatedIngredients);

  }

  purchaseCancelHandler = () => {
    this.setState({isBuying: false})
  }

  purchaseContinueHandler = () => {
    this.setState({loading : true})
    const order = {
      ingredients: this.state.ingredients,
      price: this.state.totalPrice,
      customer: {
        name: 'Eric',
        address : {
          street: 'Teststreet',
          zipcode: '97030',
          state: 'OR',
        },
        email: 'test@test.com',
      },
      deliveryMethod: 'fastest'
    }

    axios.post('/orders.json', order)
    .then(response => {
      this.setState({loading: false, isBuying: false})
    })
    .catch(err => {
      this.setState({loading: false, isBuying: false})
    });
  }

  render() {
    const disabledInfo = {
      ...this.state.ingredients
    };
    for(let key in disabledInfo) {
      disabledInfo[key] = disabledInfo[key] <= 0
    }
    let orderSummary = <OrderSummary
      price = {this.state.totalPrice.toFixed(2)}
      ingredients = {this.state.ingredients}
      purchaseCancelled = {this.purchaseCancelHandler}
      purchaseContinued = {this.purchaseContinueHandler} />;

      if (this.state.loading) {
        orderSummary = <Spinner />;
      }

    let burger = (
      <Aux>
      <Burger ingredients = {this.state.ingredients} />
    <BuildControls
      price = {`$ ${this.state.totalPrice.toFixed(2)}`}
      ingredientAdded = {this.addIngredientHandler}
      ingredientSubtracted = {this.subtractIngredientHandler}
      disabled ={disabledInfo}
      canBuy = {this.state.canBuy}
      isBuying = {this.purchaseHandler}/>
      </Aux>
    )
    return(
      <Aux>
        <Modal
          show ={this.state.isBuying}
          modalClosed = {this.purchaseCancelHandler}>
          {orderSummary}
        </Modal>

      </Aux>
    );
  }
}

export default withErrorHandler(BurgerBuilder, axios);
