import React, { Component } from 'react';

import Aux from '../../hoc/Aux'
import Burger from '../../components/Burger/Burger'
import BuildControls from '../../components/Burger/BuildControls/BuildControls'

const INGREDIENT_PRICES = {
  salad: .25,
  cheese: .25,
  meat: .75,
  bacon: .50
}

class BurgerBuilder extends Component {
  state = {
    ingredients: {
      salad: 0,
      bacon: 0,
      cheese: 0,
      meat: 0
    },
    totalPrice: .75,
    canBuy: false
  }

  updatePurchaseState () {
    const ingredients = {
      ...this.state.ingredients
    }
    const sum = Object.keys(ingredients)
      .map(igKey => {
        return ingredients[igKey]
      })
      .reduce((sum, el) => {
        return sum + el;
      }, 0);
    if (sum <= 0) {
      this.setState({canBuy: false})
    } else {
      this.setState({canBuy: true})
    }

  }

  addIngredientHandler = (type) => {
    const updatedCount = this.state.ingredients[type] + 1;
    const updatedIngredients = {
      ...this.state.ingredients
    }
    updatedIngredients[type] = updatedCount;
    const newPrice = this.state.totalPrice + INGREDIENT_PRICES[type];
    this.setState({totalPrice: newPrice, ingredients: updatedIngredients})

  }

  subtractIngredientHandler = (type) => {
    const oldCount = this.state.ingredients[type];
    if (oldCount <= 0) {
      return;
    }
    const newCount = oldCount -1
    const updatedIngredients = {
      ...this.state.ingredients
    };
    updatedIngredients[type] = newCount;
    const newPrice = this.state.totalPrice -INGREDIENT_PRICES[type];
    this.setState({totalPrice: newPrice, ingredients: updatedIngredients})

  }

  render() {
    const disabledInfo = {
      ...this.state.ingredients
    };
    for(let key in disabledInfo) {
      disabledInfo[key] = disabledInfo[key] <= 0
    }
    return(
      <Aux>
        <Burger ingredients = {this.state.ingredients} />
        <BuildControls
        price = {`$ ${this.state.totalPrice.toFixed(2)}`} ingredientAdded = {this.addIngredientHandler} ingredientSubtracted = {this.subtractIngredientHandler} disabled ={disabledInfo}/>
      </Aux>
    );
  }
}

export default BurgerBuilder;
